import matplotlib.pyplot as pltfrom Data_satistics import *from Data_load import *def dataPlot(data,plotstatistic):    '''    :param data: An Ny × Nz array containing calculated statistics    :param plotstatistic: A string specifying the type of statistic to be plotted (Mean, Variance, or Cross correlation.)    '''    #Contourplotf statistic    cs=plt.contourf(data)    #Laver en colorbar med værdier for plottet    plt.colorbar(cs)    plt.xlabel('Z - Coordinate')    plt.ylabel('Y - Coordinate')    plt.title('{0} Value of wind data through the Y-Z plane'.format(plotstatistic))    plt.show()